package variabeStudy;
public class MyClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//name, rollNum, grade
		
		//1. variable declaration(Allocating/Reversing memory)
		String studentName;
		//2. Variable Initialization(Assigning or Inserting Value)
	studentName="Suraj Mete";
	//3. Usage
	System.out.println("My name is "+studentName);
	
	//1. Variable Declaration
	int RollNum;
	//2. Variable initialization(Assign Value)
	RollNum=25;
	//3. usage
	System.out.println("my roll num "+RollNum);
	
	//1. Variable Declaration
	char grade;
	//2. Variable Initialization
		grade='B';
		//3. Usage
		System.out.println("my grade is "+grade);
	}

}
